import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  Navigate,
  useNavigate,
} from "react-router-dom";
import TaskList from "./components/TaskList";
import AddTaskForm from "./components/AddTaskForm";
import Login from "./components/login";
import SignUp from "./components/signup";
import "./App.css";

function Home() {
  let navigate = useNavigate();

  function handleGetStartedClick() {
    navigate("/login");
  }

  return (
    <main className="app-main">
      <div className="hero">
        <h1 className="hero-title">Welcome to Task Management</h1>
        <p className="hero-subtitle">Your All-in-One Task Solution</p>
        <button
          className="hero-button"
          onClick={handleGetStartedClick}
          onMouseEnter={(e) => {
            e.target.style.backgroundColor = "#DF3C5F"; // Electric Pink on hover
          }}
          onMouseLeave={(e) => {
            e.target.style.backgroundColor = "var(--primary-color)";
          }}
        >
          Get Started
        </button>
      </div>

      <div className="features">
        <div className="feature">
          <h2 className="feature-title">Effortless Task Management</h2>
          <p className="feature-description">
            Stay organized and boost your productivity effortlessly.
          </p>
        </div>
        <div className="feature">
          <h2 className="feature-title">Real-time Collaboration</h2>
          <p className="feature-description">
            Work seamlessly with your team in real-time.
          </p>
        </div>
        <div className="feature">
          <h2 className="feature-title">Beautifully Designed</h2>
          <p className="feature-description">
            Experience a modern and visually pleasing interface.
          </p>
        </div>
      </div>

      <div className="about">
        <h2 className="about-title">About Us</h2>
        <p className="about-description">
          We are dedicated to simplifying your task management experience. Our
          platform is designed to help you organize your tasks efficiently and
          collaborate seamlessly with your team. We believe that productivity
          should be effortless and enjoyable.
        </p>
      </div>
    </main>
  );
}

function App() {
  const [tasks, setTasks] = useState([]);
  const [navOpen, setNavOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogin = (token) => {
    localStorage.setItem("token", token);
    setIsLoggedIn(true);
  };

  const fetchTasks = async () => {
    // Fetch tasks from server and update state
    try {
      const response = await fetch("/api/tasks/user/:userId"); // Update with correct endpoint
      if (response.ok) {
        const data = await response.json();
        setTasks(data);
      }
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // Add a test route for navigation
  const TestNavigate = () => {
    let navigate = useNavigate();
    useEffect(() => {
      console.log("Test navigate executed");
      navigate("/login");
    }, [navigate]);
    return <div>Testing Navigation...</div>;
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
  };

  const toggleNav = () => {
    setNavOpen(!navOpen);
  };

  const ProtectedRoute = ({ children }) => {
    if (!isLoggedIn) {
      return <Navigate to="/login" />;
    }
    return children;
  };

  return (
    <Router>
      <div className="app">
        <header className="app-header">
          <nav className="navbar">
            <button onClick={toggleNav} className="hamburger">
              ☰
            </button>
            <div className={`nav-links ${navOpen ? "show" : ""}`}>
              <Link
                to="/"
                className="nav-link"
                onClick={() => setNavOpen(false)}
              >
                Home
              </Link>
              {isLoggedIn ? (
                <>
                  <Link
                    to="/tasks"
                    className="nav-link"
                    onClick={() => setNavOpen(false)}
                  >
                    Task List
                  </Link>
                  <Link
                    to="/add-task"
                    className="nav-link"
                    onClick={() => setNavOpen(false)}
                  >
                    Add Task
                  </Link>
                  <button onClick={handleLogout} className="nav-link">
                    Logout
                  </button>
                </>
              ) : (
                <>
                  <Link
                    to="/login"
                    className="nav-link"
                    onClick={() => setNavOpen(false)}
                  >
                    Login
                  </Link>
                  <Link
                    to="/signup"
                    className="nav-link"
                    onClick={() => setNavOpen(false)}
                  >
                    Sign Up
                  </Link>
                </>
              )}
            </div>
          </nav>
        </header>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/signup" element={<SignUp />} />
          <Route
            path="/tasks"
            element={
              <ProtectedRoute>
                <TaskList />
              </ProtectedRoute>
            }
          />
          <Route
            path="/add-task"
            element={
              <ProtectedRoute>
                <AddTaskForm />
              </ProtectedRoute>
            }
          />
          {/* Add other routes as needed */}
        </Routes>
        <footer className="app-footer">© 2023 Your Company Name</footer>
      </div>
    </Router>
  );
}

export default App;
