import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate,
} from "react-router-dom";
import TaskList from "./components/TaskList";
import AddTaskForm from "./components/AddTaskForm";
import Task from "./components/task";
import Login from "./components/";
import SignUp from "./components/signup";
import "./App.css";

function Home() {
  let navigate = useNavigate();

  function handleGetStartedClick() {
    navigate("/tasks");
  }

  return (
    <main className="app-main">{/* Existing Home component content */}</main>
  );
}

function App() {
  const [navOpen, setNavOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = (username, password) => {
    // Implement login logic here
    // If successful, set isLoggedIn to true
    setIsLoggedIn(true); // Placeholder, replace with actual logic
  };

  const handleSignUp = (username, password) => {
    // Implement sign-up logic here
  };

  return (
    <Router>
      <div className="app">
        <header className="app-header">
          <nav className="navbar">
            <button onClick={() => setNavOpen(!navOpen)} className="hamburger">
              â˜°
            </button>
            <div className={`nav-links ${navOpen ? "show" : ""}`}>
              {isLoggedIn ? (
                <>
                  <Link
                    to="/"
                    className="nav-link"
                    onClick={() => setNavOpen(false)}
                  >
                    Home
                  </Link>
                  <Link
                    to="/tasks"
                    className="nav-link"
                    onClick={() => setNavOpen(false)}
                  >
                    Task List
                  </Link>
                  <Link
                    to="/add-task"
                    className="nav-link"
                    onClick={() => setNavOpen(false)}
                  >
                    Add Task
                  </Link>
                  {/* Add a Logout Link or Button */}
                </>
              ) : (
                <>
                  {/* Links for Login and Signup */}
                  <Link
                    to="/login"
                    className="nav-link"
                    onClick={() => setNavOpen(false)}
                  >
                    Login
                  </Link>
                  <Link
                    to="/signup"
                    className="nav-link"
                    onClick={() => setNavOpen(false)}
                  >
                    Sign Up
                  </Link>
                </>
              )}
            </div>
          </nav>
        </header>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/tasks" element={<TaskList />} />
          <Route path="/add-task" element={<AddTaskForm />} />
          <Route path="/task/:id" element={<Task />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/signup" element={<SignUp onSignUp={handleSignUp} />} />
        </Routes>
        <footer className="app-footer">Frank Cervantes</footer>
      </div>
    </Router>
  );
}

export default App;
