import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useNavigate,
} from "react-router-dom";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import LiveChat from "react-live-chat"; // Ensure you have a valid license
import TaskList from "./components/TaskList";
import AddTaskForm from "./components/AddTaskForm";
import "./App.css";

function Home() {
  let navigate = useNavigate();

  function handleGetStartedClick() {
    navigate("/tasks");
  }

  return (
    <main className="app-main">
      {/* Hero section */}
      {/* Features section */}
      {/* About section */}
    </main>
  );
}

function Testimonials() {
  const settings = {
    // Add your slider settings here
  };

  return (
    <section className="testimonials">
      <h2>What Our Users Say</h2>
      <Slider {...settings}>
        <div>
          <p>"This app has revolutionized our workflow!" - Alex Q.</p>
        </div>
        {/* Add more testimonials here */}
      </Slider>
    </section>
  );
}

function App() {
  return (
    <Router>
      <div className="app">
        <header className="app-header">
          {/* Header content like your app name or logo */}
        </header>
        <Routes>
          <Route
            path="/"
            element={
              <>
                <Home />
                <Testimonials />
                {/* You can include other sections like Feature Walkthrough, Live Chat here */}
              </>
            }
          />
          <Route path="/tasks" element={<TaskList />} />
          <Route path="/add-task" element={<AddTaskForm />} />
        </Routes>
        <footer className="app-footer">
          <div>Task Management App Â© 2023</div>
          <div>Privacy Policy</div>
          <div>Terms of Service</div>
          <div>Contact Us</div>
        </footer>
        <LiveChat license="YOUR_LICENSE_NUMBER" />{" "}
        {/* Replace with your actual license number */}
      </div>
    </Router>
  );
}

export default App;
