import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useNavigate,
} from "react-router-dom";
import TaskList from "./components/TaskList";
import AddTaskForm from "./components/AddTaskForm";
import "./App.css"; // Make sure to import your CSS file

function App() {
  let navigate = useNavigate(); // Hook for navigation

  function handleGetStartedClick() {
    navigate("/tasks"); // Navigate to TaskList when "Get Started" is clicked
  }

  return (
    <Router>
      <div className="app">
        <header className="app-header">
          {/* Header content, like your app name or logo */}
        </header>
        <main className="app-main">
          <h1>Welcome to Task Management</h1>
          <p>Get organized and manage your tasks effortlessly.</p>
          <button onClick={handleGetStartedClick}>Get Started</button>
        </main>
        <footer className="app-footer">{/* Footer content */}</footer>
      </div>
      <Routes>
        <Route path="/tasks" element={<TaskList />} />
        <Route path="/add-task" element={<AddTaskForm />} />
      </Routes>
    </Router>
  );
}

export default App;
