import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useNavigate,
} from "react-router-dom";
import TaskList from "./components/TaskList";
import AddTaskForm from "./components/AddTaskForm";
import "./App.css";

function Home() {
  let navigate = useNavigate();

  function handleGetStartedClick() {
    navigate("/tasks");
  }

  return (
    <main className="app-main">
      <div className="hero">
        <h1>Welcome to Task Management</h1>
        <p>Your All-in-One Task Solution</p>
        <button onClick={handleGetStartedClick}>Get Started</button>
      </div>

      <div className="features">
        <div className="feature">
          <h2>Effortless Task Management</h2>
          <p>Organize your tasks and stay productive with ease.</p>
        </div>
        <div className="feature">
          <h2>Real-time Collaboration</h2>
          <p>Work together seamlessly with your team.</p>
        </div>
        <div className="feature">
          <h2>Beautifully Designed</h2>
          <p>Enjoy a modern and visually pleasing interface.</p>
        </div>
      </div>
    </main>
  );
}

function App() {
  return (
    <Router>
      <div className="app">
        <header className="app-header">
          {/* Header content, like your app name or logo */}
        </header>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/tasks" element={<TaskList />} />
          <Route path="/add-task" element={<AddTaskForm />} />
        </Routes>
        <footer className="app-footer">{/* Footer content */}</footer>
      </div>
    </Router>
  );
}

export default App;
